.tribe-events-c-subscribe-dropdown__container {
	display: none !important;
}

.join-us-btn a {
		min-width: 100px;
		min-height: 50px;
		background: #ECA3B7;
		color: white !important;
		border: 2px solid #ECA3B7;


	font-size:           22px;
  font-style: normal;
  font-weight:         500;
  line-height:         20px;
  text-align:          center;
  display:             inline-flex;
  align-items:         center;
  justify-content:     center;
  padding:             12px 25px;
  border-radius: 30px;


  cursor:              pointer;
  position:            relative;
  text-decoration:     none;
  text-shadow:         none;
  outline:             none;
  justify-content: space-around;
  transform:           perspective(1px) rotate(0.0000000001deg);
  backface-visibility: hidden;
  color: $white;
  transition: all ease-in-out .25s;	
}

.join-us-btn a:hover {
	background: white;
	color: #ECA3B7 !important;
	border-color: white;
}		</style>
